package arrayex;

import java.util.Arrays;

public class SortAlphabets {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {5,2,7,3,9};
		Arrays.sort(a);
		System.out.println("My Array"+Arrays.toString(a));
		
		 String str[]= {"Bavi","Hellen","Acci","Step"};
		 Arrays.sort(str);
		 System.out.println("My Array"+Arrays.toString(str));
	}

}
