package controlflow;

public class Switch2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int number=10;
            switch(number){
        	case 5: {
        		System.out.println("it is 5");
        		 break;
        	}
        	       
        	case 10: {
        		System.out.println("it is 10");
        		break;
        	}
 	       			
        	default:System.out.println("not present");
 	       
        }
	}

}
