package patterns;

public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,x;
		for(i=1;i<=3;i++) {
			for(j=1;j<=3;j++) {
				System.out.print(" ");
			}
			for(x=i;x<=1;x--) {
				System.out.print("  "+i);
	}
			System.out.println();
  }
	}
}
